import React, { useState, useEffect } from 'react'
import RTCMultiConnection from 'rtcmulticonnection'
import { useRouteMatch } from 'react-router'
import { Form, Button, Row, Col, Modal } from 'react-bootstrap'
import { useHistory } from 'react-router-dom'

import Toast from '../utils/notifications'

function StudentPage() {
  const history = useHistory()
  const connection = new RTCMultiConnection()
  connection.socketURL = 'https://rtcmulticonnection.herokuapp.com:443/'
  connection.session = {
    audio: true,
    video: true
  }
  connection.sdpConstraints.mandatory = {
    OfferToReceiveAudio: true,
    OfferToReceiveVideo: true,
  }
  const userState = history?.location?.state?.user || {
    name: '',
    camera: true,
    micro: false,
    role: 'guest'
  }
  const [user, setUser] = useState(userState)
  const match = useRouteMatch()
  let roomId = match?.params?.uuid || 1

  const [show, setShow] = useState(false)

  useEffect(() => {
    if(!history?.location?.state?.user) {
      setShow(true)
    } else {
      connection.openOrJoin(roomId)
    }
  }, [roomId])

  const handleChangeUser = (name, value) => {
    setUser(prevTags => {
      return {
        ...prevTags,
        [name]: value
      }
    })
  }
  const handleChangeSwitch = (name) => {
    setUser(prevTags => {
      return {
        ...prevTags,
        [name]: !prevTags[name]
      }
    })
  }

  const handleClose = () => setShow(false)
  // const handleShow = () => setShow(true)

  const handleSubmit = (event) => {
    event.preventDefault()
    console.log('connection', connection)
    let numberOfUsers = connection.getAllParticipants().length
    console.log('numberOfUsers', numberOfUsers)

    connection.user = user

    console.log('connection getAllParticipants', connection.getAllParticipants())

    // roomId = connection.token()
    connection.openOrJoin(roomId)
    Toast.success('You are joining the class!!!', '')
    history.push(history.location.pathname, {user})
    handleClose()
    return false
  }
  console.log('connection getAllParticipants', connection.getAllParticipants())

  return (
    <div>
      <Modal
        show={show}
        onHide={handleClose}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
      >
        <Modal.Header closeButton>
          <Modal.Title>JOIN CLASS ROOM</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={(event) => handleSubmit(event)}>
            <Form.Group as={Row} controlId="formHorizontalEmail">
              <Form.Label column sm={2}>
                Name
              </Form.Label>
              <Col sm={10}>
                <Form.Control
                  type="text"
                  placeholder="Enter your name"
                  value={user.name}
                  onChange={(event) => handleChangeUser('name', event.target.value)}
                />
              </Col>
            </Form.Group>

            <Form.Group as={Row} controlId="formHorizontalEmail">
              <Form.Label column sm={2}>
                Camera
              </Form.Label>
              <Col sm={10}>
                <Form.Check
                  type="switch"
                  id="camera"
                  label="ON/OFF"
                  value={user.camera}
                  onChange={(event) => handleChangeSwitch('camera')}
                />
              </Col>
            </Form.Group>

            <Form.Group as={Row} controlId="formHorizontalEmail">
              <Form.Label column sm={2}>
                micro
              </Form.Label>
              <Col sm={10}>
                <Form.Check
                  type="switch"
                  id="micro"
                  label="ON/OFF"
                  value={user.micro}
                  onChange={(event) => handleChangeSwitch('micro')}
                />
              </Col>
            </Form.Group>

            <Form.Group as={Row}>
              <Col sm={{ span: 10, offset: 2 }}>
                <Button type="submit">Join room</Button>
              </Col>
            </Form.Group>
          </Form>
        </Modal.Body>
      </Modal>

      <Row className="justify-content-md-center">
        <Col xs lg="6">
          <hr />
          <h4>Student: {user.name}</h4>
          <hr />
        </Col>
      </Row>
    </div>
  )
}

export default StudentPage
