import React, { useState, useEffect, useRef } from 'react'
import RTCMultiConnection from 'rtcmulticonnection'
import { useRouteMatch } from 'react-router'
import { Form, Button, Row, Col, Modal } from 'react-bootstrap'
import { useHistory } from 'react-router-dom'

import Toast from '../utils/notifications'

function StudentPage() {
  const history = useHistory()
  const match = useRouteMatch()
  const userState = history?.location?.state?.user || {
    name: '',
    camera: true,
    micro: true,
    role: 'guest'
  }

  const [user, setUser] = useState(userState)
  const [show, setShow] = useState(false)

  // eslint-disable-next-line
  const connection = new RTCMultiConnection()
  connection.socketURL = 'https://rtcmulticonnection.herokuapp.com:443/'

  connection.session = {
    audio: userState.micro,
    video: userState.camera,
    data: true
  }

  connection.sdpConstraints.mandatory = {
    OfferToReceiveAudio: true,
    OfferToReceiveVideo: true
  }

  connection.mediaConstraints = {
      video: true,
      audio: true
  }
  console.log('connection.channel', connection.channel)

  const refLocal = useRef(null)
  const refRemote = useRef(null)

  connection.onstream = (event) => {
    delete event.mediaElement.id
    const video = event.mediaElement

    console.log('connection.socket', connection.socket)

    if(event.type === 'local') {
      refLocal.current.id = event.streamId
      refLocal.current.appendChild(video)
    }
    if(event.type === 'remote') {
      refLocal.current.id = event.streamId
      refRemote.current.appendChild(video)
    }
  }

  connection.extra = {
    ...user
  }
  // const userPreferences = connection.setUserPreferences({
  //   extra: connection.extra,
  //   localPeerSdpConstraints: {
  //       OfferToReceiveAudio: true,
  //       OfferToReceiveVideo: true
  //   },
  //   remotePeerSdpConstraints: {
  //       OfferToReceiveAudio: true,
  //       OfferToReceiveVideo: true
  //   },
  //   isOneWay: false,
  //   isDataOnly: false,
  //   dontGetRemoteStream: false,
  //   dontAttachLocalStream: false,
  //   connectionDescription: {
  //       remoteUserId: connection.userid,
  //       message: {
  //           newParticipationRequest: true,
  //           isOneWay: false,
  //           isDataOnly: false,
  //           localPeerSdpConstraints: {
  //               OfferToReceiveAudio: true,
  //               OfferToReceiveVideo: true
  //           },
  //           remotePeerSdpConstraints: {
  //               OfferToReceiveAudio: true,
  //               OfferToReceiveVideo: true
  //           }
  //       },
  //       sender: connection.userid
  //   },
  //   successCallback: function() {
  //       //
  //   }
  // })

  let roomId = match?.params?.uuid || 1

  useEffect(() => {
    if(!history?.location?.state?.user) {
      setShow(true)
    } else {
      console.log('saaaa');
      console.log('openOrJoin', roomId)
      connection.openOrJoin(roomId)
      connection.acceptParticipationRequest(connection.userid, userPreferences)
      // handleClose()
      Toast.success('You are joining the class!!!', '')
    }
  }, [roomId])

  const handleChangeUser = (name, value) => {
    setUser(prevTags => {
      return {
        ...prevTags,
        [name]: value
      }
    })
  }
  const handleChangeSwitch = (name, value) => {
    setUser(prevTags => {
      return {
        ...prevTags,
        [name]: !value
      }
    })
  }

  const handleClose = () => setShow(false)
  // const handleShow = () => setShow(true)

  const handleSubmit = (event) => {
    event.preventDefault()
    connection.user = user
    console.log('user', user)
    history.push(history.location.pathname, {user})
    console.log('openOrJoin', roomId)
    connection.openOrJoin(roomId)
    handleClose()
    Toast.success('You are joining the class!!!', '')
    connection.acceptParticipationRequest(connection.userid, userPreferences)
    return false
  }

  // connection.getAllParticipants().forEach(function(participantId) {
  //   let user = connection.peers[participantId];
  //   console.log('getAllParticipants', user);
  // });

  // console.log('connection', connection)
  // console.log('getAllParticipants', connection.getAllParticipants().length)

  return (
    <div>
      <Modal
        show={show}
        onHide={handleClose}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
      >
        <Modal.Header closeButton>
          <Modal.Title>JOIN CLASS ROOM</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={(event) => handleSubmit(event)}>
            <Form.Group as={Row} controlId="formHorizontalEmail">
              <Form.Label column sm={2}>
                Name
              </Form.Label>
              <Col sm={10}>
                <Form.Control
                  type="text"
                  placeholder="Enter your name"
                  value={user.name}
                  onChange={(event) => handleChangeUser('name', event.target.value)}
                />
              </Col>
            </Form.Group>

            <Form.Group as={Row} controlId="formHorizontalEmail">
              <Form.Label column sm={2}>
                Camera
              </Form.Label>
              <Col sm={10}>
                <Form.Check
                  type="switch"
                  id="camera"
                  label="ON/OFF"
                  checked={user.camera}
                  onChange={() => handleChangeSwitch('camera', user.camera)}
                />
              </Col>
            </Form.Group>

            <Form.Group as={Row} controlId="formHorizontalEmail">
              <Form.Label column sm={2}>
                micro
              </Form.Label>
              <Col sm={10}>
                <Form.Check
                  type="switch"
                  id="micro"
                  label="ON/OFF"
                  checked={user.micro}
                  onChange={() => handleChangeSwitch('micro', user.micro)}
                />
              </Col>
            </Form.Group>

            <Form.Group as={Row}>
              <Col sm={{ span: 10, offset: 2 }}>
                <Button type="submit">Join room</Button>
              </Col>
            </Form.Group>
          </Form>
        </Modal.Body>
      </Modal>

      <Row className="justify-content-md-center">
        <Col xs lg="12">
          <h4>Student</h4>
          <div ref={refRemote}></div>
        </Col>
        <Col xs lg="12">
          <h4>Teacher</h4>
          {/* <div ref={refRemote}></div> */}
        </Col>
      </Row>

      <Row className="justify-content-md-center">
        <Col xs lg="6">
          <hr />
          <div ref={refLocal}></div>
          <p>Student: {user.name}</p>
          <hr />
        </Col>
      </Row>
    </div>
  )
}

export default StudentPage
